import './SubmitPage.css'
import { calculateResortsDriving } from '../scripts/drivingTimeLogic';
import PossibleTrip from '../components/PossibleTrip';
import { useState } from 'react';
import Dropdown from '../components/Dropdown';

function SubmitPage({ data }) {
  const resorts = calculateResortsDriving(data.Region, data["Zip Code"]);
  const [formValues, setFormValues] = useState({});

  const handleFormChange  = (title , value) => {
    setFormValues( prev => ({ ...prev, [title]: value}));
};

  const handleSubmit = async (e) =>  {
    e.preventDefault();
    const formData = new FormData(e.target);
    const formObject = { ...Object.fromEntries(formData.entries()), ...formValues };

        // Simulate API call
        const response = await new Promise((resolve) =>
            setTimeout(() => resolve({ success: true, data: formObject }), 1500)
        );
        console.log(formValues);
  };

  let header = `Available ski trips in the ${data.Region}`
  header += data["Zip Code"] ? ` leaving from ${data["Zip Code"]}:` : ':'; 

      return (
        <div className="submit-page">
          <h2>Your Submission Details</h2>
          <div className="data-card">
            <div className="data-row">
              <span className="data-label">Region:</span>
              <span className="data-value">{data.Region || "N/A"}</span>
            </div>
            <div className="data-row">
              <span className="data-label">Fly or not</span>
              <span className="data-value">{data["Willing to fly?"] ? "Yes" : "No"}</span>
            </div>
            <div className="data-row">
              <span className="data-label">Stay:</span>
              <span className="data-value">{data.Stay || "N/A"}</span>
            </div>
            <div className="data-row">
              <span className="data-label">Zip Code:</span>
              <span className="data-value">{data["Zip Code"] || "N/A"}</span>
            </div>
            <div className="data-row">
              <span className="data-label">Number of Guests:</span>
              <span className="data-value">{data["Number of Guests"] || "N/A"}</span>
            </div>
            <div className="data-row">
              <span className="data-label">Epic Pass:</span>
              <span className="data-value">{data["Epic Pass"] ? "Yes" : "No"}</span>
            </div>
            <div className="data-row">
              <span className="data-label">Ikon Pass:</span>
              <span className="data-value">{data["Ikon Pass"] ? "Yes" : "No"}</span>
            </div>
            <div className="data-row">
              <span className="data-label">Start Date:</span>
              <span className="data-value">{data["Start Date"] || "N/A"}</span>
            </div>
            <div className="data-row">
              <span className="data-label">End Date:</span>
              <span className="data-value">{data["End Date"] || "N/A"}</span>
            </div>
          </div>

          <div className='data-handling'>
            <h2>{header}</h2>
            <form onChange={handleSubmit}>  
              <Dropdown options={["Relevant", "Distance", "Price", "Most Trails"]} label={"Sort By"} onChange={handleFormChange}/>
            </form>

              {resorts.length > 0 ? (
              <div>
                <div className='resort-grid'>
                {resorts.map((element, index) => (
                    <PossibleTrip key={index} name={element.name} drivingTime={element.drivingTime}/>
                ))}
                </div>
              </div>
            ) : (
              <div>
                <h1>Invalid zip code. try again</h1>
              </div>
            )}
          </div>
          </div>
      )
    }

    export default SubmitPage;
