import { useEffect, useMemo, useRef, useState } from "react";
import Particles, { initParticlesEngine } from "@tsparticles/react";
import { loadSlim } from "@tsparticles/slim";
import './HomePage.css';
import backgroundVideo from '../media/ski-video.mp4';
import FormInput from '../components/FormInput';

function HomePage() {
    const [init, setInit] = useState(false);

    useEffect(() => {
        initParticlesEngine(async (engine) => {
            await loadSlim(engine);
        }).then(() => {
            setInit(true);
        });
    }, []);

    const options = useMemo(
    () => ({
        autoPlay: true,
        clear: true,
        delay: 0.1,
        detectRetina: true,
        fpsLimit: 120,
        interactivity: {
            events: {
                onClick: { 
                    enable: true, 
                    mode: "push" 
                }, 
                onHover: { enable: true, mode: "repulse" },
            },
            modes: {
                push: { quantity: 4 }, 
                repulse: { distance: 260, duration: 0.4 }, 
            },
        },
        particles: {
            color: { value: "rgba(255, 255, 255, 0)" },
            links: {
                color: "#ffffff",
                distance: 150,
                enable: true,
                opacity: 0.9,
                width: 5,
            },
            move: {
                direction: "none",
                enable: true,
                outModes: { default: "bounce" },
                random: false,
                speed: 6,
                straight: false,
            },
            number: {
                density: { enable: true },
                value: 60,
            },
            opacity: { value: 0.8 },
            shape: { type: "circle" },
            size: { value: { min: 2, max: 7 } },
        },
        pauseOnBlur: true,
        pauseOnOutsideViewport: false,
        responsive: [],
        smooth: true,
        style: {},
        themes: [],
        zLayers: 1,
    }),
    []
);

    // handling of forms

    const handleSubmit = (e) => {
        e.preventDefault();
        const data = new FormData(e.target)
        console.log(Object.fromEntries(data.entries()))
    }

    return (
        <div className='outer'>
            <div className="video-container">
                <video autoPlay muted loop playsInline>
                    <source src={backgroundVideo} type="video/mp4" />
                    Ski Video
                </video>
                <div className="video-overlay"></div>
            </div>

            <div className='nav'>
                    <ul>
                        <li>Home</li>
                        <li>About</li>
                        <li>Other</li>
                        <li>Main Tool</li>
                    </ul>
            </div>

            <div 
    id="particles-container" 
    onClick={(e) => {
        if (document.querySelector('.input-form')?.contains(e.target)) {
            e.stopPropagation(); // Prevent particle spawning when clicking on the form
        }
    }}
>
    {init && <Particles id="tsparticles" options={options} />}
</div>

          {/* Form for Zip Code */}

            <div className="input-form">
                <form onSubmit={handleSubmit}>
                    <FormInput name="username" placeholder="username"/>
                    <FormInput name="email" placeholder="email"/>
                    <FormInput name = "fullname" placeholder="12345"/>
                    <button>Submit</button>
                </form>
            </div>
        </div>

    );
}

export default HomePage;
