import { useState } from "react";

function CheckBox(props) {
    const[isTrue, setTrue] = useState(false);
    const [message, setMessage] = useState(`□ ${props.messsage}`);


    const toggleTrue = () => {
        setTrue(!isTrue);
        setMessage(isTrue ? `□ ${props.message}` : `▣ ${props.messsage}`);
    };

    return ( 
        <div>
            <button onClick={toggleTrue}>Hi</button>
        </div>
    );
}

export default CheckBox;
