import { useEffect, useMemo, useState } from "react";
import Particles, { initParticlesEngine } from "@tsparticles/react";
import { loadSlim } from "@tsparticles/slim";
import './HomePage.css';
import testImage from './test-image.png';

function HomePage() {
    const [init, setInit] = useState(false);

    useEffect(() => {
        initParticlesEngine(async (engine) => {
            await loadSlim(engine);
        }).then(() => {
            setInit(true);
        });
    }, []);

    const particlesLoaded = (container) => {
        // You can handle the loaded particles event here if needed
    };

    const options = useMemo(
        () => ({
            autoPlay: true,
            background: {
                color: {
                    value: "#0d47a1", // Background color
                },
                image: "", // If you want to use an image, add the image URL here
            },
            backgroundMask: {
                enable: true,
                cover: {
                    value: "#ffffff", // Mask color
                },
                image: {
                    src: testImage, // Optional image as mask
                    width: "50%",
                    height: "50%",
                },
            },
            clear: false,
            delay: 0.1, // Delay for starting the animation
            detectRetina: true,
            fpsLimit: 120, // Set FPS limit for smoother animation
            interactivity: {
                events: {
                    onClick: { enable: true, mode: "push" }, // Push more particles on click
                    onHover: { enable: true, mode: "repulse" }, // Repulse particles on hover
                },
                modes: {
                    push: { quantity: 4 }, // Number of particles to push on click
                    repulse: { distance: 10, duration: 0.4 }, // Repulsion effect distance and duration
                },
            },
            particles: {
                color: { value: "#ffffff" }, // Particle color
                links: {
                    color: "#ffffff", // Link color
                    distance: 150, // Distance between linked particles
                    enable: true, // Enable particle links
                    opacity: 0.5, // Link opacity
                    width: 1, // Link width
                },
                move: {
                    direction: "none", // No specific direction for particle movement
                    enable: true,
                    outModes: { default: "bounce" }, // Bounce off the edges of the canvas
                    random: false,
                    speed: 6, // Particle movement speed
                    straight: false,
                },
                number: {
                    density: { enable: true }, // Enable density control
                    value: 190, // Number of particles
                },
                opacity: { value: 0.5 }, // Particle opacity
                shape: { type: "circle" }, // Shape of particles
                size: { value: { min: 1, max: 5 } }, // Particle size range
            },
            pauseOnBlur: true, // Pause particles animation on window blur
            pauseOnOutsideViewport: false, // Pause when the canvas goes outside the viewport
            responsive: [], // Responsive settings (optional)
            smooth: true, // Enable smooth animations
            style: {}, // Add additional styles here if needed
            themes: [], // Theme options if required
            zLayers: 1, // Number of layers for particles
        }),
        []
    );

    return (
        <div className='outer'>
            <div className='nav'>
                <ul>
                    <li>Home</li>
                    <li>About</li>
                    <li>Other</li>
                    <li>Main Tool</li>
                </ul>
            </div>

            {init && (
                <Particles
    id="tsparticles"
    particlesLoaded={particlesLoaded}
    options={options}
    style={{
        position: "absolute",
        top: 0,
        left: 0,
        width: "100%",
        height: "100%",
        zIndex: 1,
    }}
/>
            )}
        </div>
    );
}

export default HomePage;
