import './SubmitPage.css'
import { useState } from 'react';
import { findZipcodeLocation } from '../scripts/zip-checker';

function SubmitPage({ data }) {
  const [zipCode, setZipCode] = useState('');  
  const [location, setLocation] = useState(null);

  const handleZipCodeChange = (e) => {
    setZipCode(e.target.value);
  };

  const handleFindLocation = () => {
    const locationData = findZipcodeLocation(zipCode); 
    if (locationData) {
      setLocation(locationData); 
    } else {
      setLocation(null);  // If not found, clear location
      alert('Zip code not found.');
    }
  };


      // Assuming 'data' is an object with properties like 'region', 'stay', 'zipCode', etc.
      return (
        <div className="submit-page">
          <h2>Your Submission Details</h2>
          <div className="data-card">
            <div className="data-row">
              <span className="data-label">Region:</span>
              <span className="data-value">{data.Region || "N/A"}</span>
            </div>
            <div className="data-row">
              <span className="data-label">Fly or not</span>
              <span className="data-value">{data["Willing to fly?"] ? "Yes" : "No"}</span>
            </div>
            <div className="data-row">
              <span className="data-label">Stay:</span>
              <span className="data-value">{data.Stay || "N/A"}</span>
            </div>
            <div className="data-row">
              <span className="data-label">Zip Code:</span>
              <span className="data-value">{data["Zip Code"] || "N/A"}</span>
            </div>
            <div className="data-row">
              <span className="data-label">Number of Guests:</span>
              <span className="data-value">{data["Number of Guests"] || "N/A"}</span>
            </div>
            <div className="data-row">
              <span className="data-label">Epic Pass:</span>
              <span className="data-value">{data["Epic Pass"] ? "Yes" : "No"}</span>
            </div>
            <div className="data-row">
              <span className="data-label">Ikon Pass:</span>
              <span className="data-value">{data["Ikon Pass"] ? "Yes" : "No"}</span>
            </div>
            <div className="data-row">
              <span className="data-label">Start Date:</span>
              <span className="data-value">{data["Start Date"] || "N/A"}</span>
            </div>
            <div className="data-row">
              <span className="data-label">End Date:</span>
              <span className="data-value">{data["End Date"] || "N/A"}</span>
            </div>
          </div>

            <div className='data-handling'>
              <h1>Zip Code to Latitude/Longitude</h1>
            </div>
          </div>
      );
    }

    export default SubmitPage;
