import { useEffect, useMemo, useState } from "react";
import Particles, { initParticlesEngine } from "@tsparticles/react";
import { loadSlim } from "@tsparticles/slim";
import './HomePage.css';
import backgroundVideo from '../media/ski-video.mp4';
import FormInput from '../components/FormInput';
import Dropdown from "../components/Dropdown";
import CheckBox from "../components/CheckBox";
import DateSelector from "../components/DateSelector";
import SubmitPage from "./SubmitPage";

function HomePage() {
    const [init, setInit] = useState(false);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);
    const [data, setData] = useState(null);
    const [formValues, setFormValues] = useState({});

    const handleFormChange  = (title , value) => {
        setFormValues( prev => ({ ...prev, [title]: value}));
    };

    useEffect(() => {
        initParticlesEngine(async (engine) => {
            await loadSlim(engine);
        }).then(() => {
            setInit(true);
        });
    }, []);

    const options = useMemo(
        () => ({
            autoPlay: true,
            clear: true,
            delay: 0.1,
            detectRetina: true,
            fpsLimit: 120, 
            interactivity: {
                events: {
                    onClick: { enable: false, mode: "push" }, 
                    onHover: { enable: true, mode: "repulse" }, 
                },
                modes: {
                    push: { quantity: 4 }, 
                    repulse: { distance: 260, duration: 0.4 }, 
                },
            },
            particles: {
                color: { value: "rgba(255, 255, 255, 0)" }, 
                links: {
                    color: "#ffffff", // Link color
                    distance: 150, // Distance between linked particles
                    enable: true, // Enable particle links
                    opacity: 0.9, // Link opacity
                    width: 5, // Link width
                },
                move: {
                    direction: "none", // No specific direction for particle movement
                    enable: true,
                    outModes: { default: "bounce" }, // Bounce off the edges of the canvas
                    random: false,
                    speed: 6, // Particle movement speed
                    straight: false,
                },
                number: {
                    density: { enable: true }, // Enable density control
                    value: 60, // Number of particles
                },
                opacity: { value: 0.8 }, // Particle opacity
                shape: { type: "circle" }, // Shape of particles
                size: { value: { min: 2, max: 7 } }, // Particle size range
            },
            pauseOnBlur: true, // Pause particles animation on window blur
            pauseOnOutsideViewport: false, // Pause when the canvas goes outside the viewport
            responsive: [], // Responsive settings (optional)
            smooth: true, // Enable smooth animations
            style: {}, // Add additional styles here if needed
            themes: [], // Theme options if required
            zLayers: 1, // Number of layers for particles
        }),
        []
    );

    const handleSubmit = async (e) =>  {
        e.preventDefault();
        setIsLoading(true);
        setError(null);
        const formData = new FormData(e.target);
        const formObject = { ...Object.fromEntries(formData.entries()), ...formValues };

        console.log("Submitting Form Data:", formObject);

        try {
            // Simulate API call
            const response = await new Promise((resolve) =>
                setTimeout(() => resolve({ success: true, data: formObject }), 1500)
            );

            if (response.success) {
                setData(response.data);  // Store the data for NewPage
            } else {
                throw new Error("Failed to fetch data");
            }
        } catch (err) {
            setError(err.message);
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className='outer'>
            <div className="video-container">
                <video autoPlay muted loop playsInline>
                    <source src={backgroundVideo} type="video/mp4" />
                    Ski Video
                </video>
                <div className="video-overlay"></div>
            </div>

            <div className='nav'>
                    <ul>
                        <li>Home</li>
                        <li>About</li>
                        <li>Other</li>
                        <li>Main Tool</li>
                    </ul>
            </div>

            {init && ( <Particles id="tsparticles" options={options}/>)}

         { data ? ( 
            <SubmitPage data={data} object={formObject}/>
         ) : (
            <div className="input-form">
                <form onSubmit={handleSubmit}>
                    <div className="row-container">
                        <Dropdown options={["All", "Northeast", "South", "West", "Canada"]} label={"Region"} onChange={handleFormChange}/>
                        <Dropdown options={["AirBnB", "Hotel", "Both"]} label={"Stay"} onChange={handleFormChange}/>
                    </div>
                    <div className="row-container">
                        <FormInput name="Zip Code" placeholder="e.g. 12345" min='5' max='5' type='text'/>
                        <FormInput name='Number of Guests' label='# of guests' type='number' maxValue='1,000,000'/>

                        <CheckBox name={"Epic Pass"} onChange={handleFormChange}/>
                        <CheckBox name={"Ikon Pass"} onChange={handleFormChange}/>
                    </div>
                    <div className="row-container">
                        <DateSelector name="Start Date" onChange={handleFormChange}/>
                        <DateSelector name="End Date" onChange={handleFormChange}/>
                    </div>
                    <button type="submit">Find Best Trip...</button>
                </form>
            </div>
         ) } ;
        </div>
    );
}

export default HomePage;
