import { useEffect, useMemo, useState } from "react";
import Particles, { initParticlesEngine } from "@tsparticles/react";
import { loadSlim } from "@tsparticles/slim";
import './HomePage.css';

function HomePage() {
    const [init, setInit] = useState(false);

    useEffect(() => {
        initParticlesEngine(async (engine) => {
            await loadSlim(engine);
        }).then(() => {
            setInit(true);
        });
    }, []);

    const particlesLoaded = (container) => {
    };

    const options = useMemo(
        () => ({
            background: {
                color: {
                    value: "#0d47a1",
                },
                image: "", // If you want to use an image as the background
            },
            backgroundMask: {
                enable: true, // Enable the background mask
                cover: {
                    value: "#ffffff", // This is the color that will be used for the mask
                },
                image: {
                    src: "./", // Use an image as a mask
                    width: "100%",
                    height: "100%",
                },
            },
            fpsLimit: 120,
            interactivity: {
                events: {
                    onClick: { enable: true, mode: "push" },
                    onHover: { enable: true, mode: "repulse" },
                },
                modes: {
                    push: { quantity: 4 },
                    repulse: { distance: 200, duration: 0.4 },
                },
            },
            particles: {
                color: { value: "#ffffff" },
                links: {
                    color: "#ffffff",
                    distance: 150,
                    enable: true,
                    opacity: 0.5,
                    width: 1,
                },
                move: {
                    direction: "none",
                    enable: true,
                    outModes: { default: "bounce" },
                    random: false,
                    speed: 6,
                    straight: false,
                },
                number: {
                    density: { enable: true },
                    value: 80,
                },
                opacity: { value: 0.5 },
                shape: { type: "circle" },
                size: { value: { min: 1, max: 5 } },
            },
            detectRetina: true,
        }),
        []
    );


    return (
        <div className='outer'>
            <div className='nav'>
                <ul>
                    <li>Home</li>
                    <li>About</li>
                    <li>Other</li>
                    <li>Main Tool</li>
                </ul>
            </div>

            {init && (
                <Particles
                    id="tsparticles"
                    particlesLoaded={particlesLoaded}
                    options={options}
                />
            )}
        </div>
    );
}

export default HomePage;
