import { useState } from "react";
import "./CheckBox.css"; // Optional for styling

function CheckBox({ label }) {
    const [checked, setChecked] = useState(false);

    const toggleChecked = () => {
        setChecked((prev) => !prev);
    };

    return (
        <button className="checkbox-button" onClick={toggleChecked}>
            {checked ? "☑" : "☐"} {label.message}
        </button>
    );
}

export default CheckBox;
